declare namespace TopSinHocon {
    /**
     * 将一个hocon字符串转为json字符串
     * str: hocon
     * formatted: 是否格式化（默认为false）
     * @returns 转换后的json字符串,如果出错则为空，可通过ok()检查是否成功, message()打印错误消息
     */
    export function parseString(str: string, formatted: boolean):string;

    /**
     * 合并多个hocon字符串为一个json字符串
     * strlst: hocon字符串列表
     * formatted: 同上
     */
    export function parseStrings(strlst: Array<string>, formatted: boolean):string;

    /**
     * 将包含hocon字符串的文件转为json字符串
     * path: hocon配置的路径
     * formatted: 同上
     */
    export function parseFile(path: string, formatted: boolean):string;

    /**
     * 将多个hocon配置文件合并为一个json字符串
     * pathlst: hocon配置文件列表
     * formatted: 同上
     */
    export function parseFiles(pathlst: Array<string>, formatted: boolean):string;

    /**
     * hocon转json是否成功，成功为true
     */
    export function ok():boolean

    /**
     * 返回错误消息，成功为空
     */
    export function message():string
}

export = TopSinHocon;