declare namespace TopSinLogger {

    /**
     * 载入日志配置
     * @param iParam 
     */
    export function loadConfig(iParam:Map<string,any>):null
    
    /**
     * 输出崩溃日志
     * @param iMsg 消息内容
     * @param iCategory 分类
     * @param iFile 文件
     * @param iFunc 函数
     */
    export function fatal(iMsg:string, iCategory?:string, iFile?:string, iFunc?:string):boolean;

    /**
     * 输出错误日志
     * @param iMsg 消息内容
     * @param iCategory 分类
     * @param iFile 文件
     * @param iFunc 函数
     */
    export function error(iMsg:string, iCategory?:string, iFile?:string, iFunc?:string):boolean;

    /**
     * 输出警告日志
     * @param iMsg 消息内容
     * @param iCategory 分类
     * @param iFile 文件
     * @param iFunc 函数
     */
    export function warn(iMsg:string, iCategory?:string, iFile?:string, iFunc?:string):boolean;

    /**
     * 输提示日志
     * @param iMsg 消息内容
     * @param iCategory 分类
     * @param iFile 文件
     * @param iFunc 函数
     */
    export function info(iMsg:string, iCategory?:string, iFile?:string, iFunc?:string):boolean;

    /**
     * 输出SQL日志
     * @param iMsg 消息内容
     * @param iCategory 分类
     * @param iFile 文件
     * @param iFunc 函数
     */
    export function sql(iMsg:string, iCategory?:string, iFile?:string, iFunc?:string):boolean;

    /**
     * 输出调试日志
     * @param iMsg 消息内容
     * @param iCategory 分类
     * @param iFile 文件
     * @param iFunc 函数
     */
    export function debug(iMsg:string, iCategory?:string, iFile?:string, iFunc?:string):boolean;

    /**
     * 输出跟踪日志
     * @param iMsg 消息内容
     * @param iCategory 分类
     * @param iFile 文件
     * @param iFunc 函数
     */
    export function trace(iMsg:string, iCategory?:string, iFile?:string, iFunc?:string):boolean;

    /**
     * 输出日志
     * @param iCategory 消息分类 
     * @param iType 消息类型 FATAL,ERROR,WARN,INFO,SQL,DEBUG,TRACE
     * @param iMsg 内容
     * @param iFile 文件
     * @param iLine 行数
     * @param iFunc 函数 
     * @param iThread 线程
     */
    export function writeMsg(iCategory:string, iType:string, iMsg:string, iFile?:string, iLine?:number, iFunc?:string, iThread?:string):boolean;
    
}

export = TopSinLogger;
