import * as TopSinError from "topsin.error";
declare namespace TopSinMail {
    export var AuthMethod = {LOGIN:'LOGIN', PLAIN:'PLAIN',NTLM:'NTLM', CRAM_MD5:'CRAM-MD5',NONE:'None'}
    export type AuthMethod = "LOGIN" | "PLAIN" | "NTLM" | "CRAM-MD5" | "None";
    export var ConnectionType = {TCP:'TCP', SSL:'SSL', TLS:'TLS'};
    export type ConnectionType =  "TCP" | "SSL" | "TLS";

    export interface MailSenderParam {
        host:string,
        port:number,
        connection_type:ConnectionType,
        user:string,
        password:string,
        auth_method:AuthMethod,
        connection_timeout:number,
        response_timeout:number,
        from:string,
        to:string|Array<string>,
        cc:string|Array<string>,
        bcc:string|Array<string>,
        subject:string,
        content:string,
        attachment:string|Array<string>,
    };

    /**
     * 发送邮件;
     */
    export function sendMail(iParam:MailSenderParam):TopSinError;


}

export = TopSinMail;

