declare class TopSinError {

    /**
     * 创建TError对象;
     * 
     * @param text 错误描述
     * @param type 错误类型
     * @param code 错误代号
     * @param trace 错误追溯信息
     */
    constructor(text?:string,type?:string,code?:string,trace?:string);

    /**
     * 设置错误内容;
     * 
     * @param text 错误描述
     * @param type 错误类型
     * @param code 错误代号
     * @param trace 错误追溯信息
     */
    setError(text?:string,type?:string,code?:string,trace?:string):this;

    /**
     * 清除错误内容;
     */
    clearError():this;

    /**
     * 获取错误文本信息;
     */
    text():string

    /**
     * 设置错误文本信息;
     * 
     * @param text 错误文本信息
     */
    setText(text:string):this;

    /**
     * 获取错误代码;
     */
    code():string;

    /**
     * 设置错误代号;
     * 
     * @param code 错误代号
     */
    setCode(code:string):this;

    /**
     * 获取错误类型;
     */
    type():string;

    /**
     * 设置错误类型;
     * @param type 错误类型
     */
    setType(type:string):this;

    /**
     * 获取错误追溯信息;
     */
    trace():string;

    /**
     * 设置错误追溯信息;
     * 
     * @param trace 追溯信息
     */
    setTrace(trace:string):this;

    /**
     * 判断错误是否有效;
     */
    isValid():boolean;

    /**
     * 复制错误;
     */
    clone():TopSinError;


}

export = TopSinError;
